/* ifcTroblDiskAjax */ SELECT 'IFC' JOBSE, #troblcolctse# TROBLCOLCTSE, #detailclse# DETAILCLSE, C.REAL_HOSTNAME, #troblgradse# TROBLGRADSE, '0' TROBLPROCESSSTTUS, #detailclse# || ' ' || #thrhld# || '% 초과' TROBLCN, MAX(C.HOSTNAME) || ' DISK(' || MAX(A.ETC1) || ') : ' || MAX(A.DISK) || '%' AS RM, MAX(A.INST_DATE) INST_DATE, MAX(C.HOSTNAME) HOSTNAME, MAX(A.DISK) DISK FROM (SELECT REAL_HOSTNAME, DISK, ETC1, INST_DATE FROM IOC_SDTIC_IFC_COLCT_DATA) A, (SELECT REAL_HOSTNAME, MAX(INST_DATE) MAX_INST_DATE FROM IOC_SDTIC_IFC_COLCT_DATA GROUP BY (REAL_HOSTNAME)) B, IOC_SDTIC_IFC_HOSTNAME_COLCT C WHERE A.REAL_HOSTNAME = B.REAL_HOSTNAME AND A.INST_DATE = B.MAX_INST_DATE AND A.REAL_HOSTNAME = C.REAL_HOSTNAME AND A.DISK >= TO_NUMBER(#thrhld#) GROUP BY C.REAL_HOSTNAME
UNION ALL
SELECT 'IFC' JOBSE, #troblcolctse# TROBLCOLCTSE, #detailclse# DETAILCLSE, A.GROUP2 REAL_HOSTNAME, #troblgradse# TROBLGRADSE, '0' TROBLPROCESSSTTUS, #detailclse# || ' ' || #thrhld# || '% 초과' TROBLCN, MAX(A.GROUP2_NAME) || ' DISK : ' || MAX(A.DISK) || '%' AS RM, MAX(A.INST_DATE) INST_DATE, MAX(A.GROUP2_NAME) HOSTNAME, MAX(A.DISK) DISK FROM (SELECT GROUP2, GROUP2_NAME, DISK, INST_DATE, NVL(ETC,'0') ETC FROM IOC_SDTIC_IFC_SPT_COLCT_DATA) A, (SELECT GROUP2, MAX(INST_DATE) MAX_INST_DATE, NVL(ETC,'0') ETC FROM IOC_SDTIC_IFC_SPT_COLCT_DATA GROUP BY (GROUP2, ETC)) B WHERE A.GROUP2 = B.GROUP2 AND A.INST_DATE = B.MAX_INST_DATE AND A.ETC = B.ETC AND A.DISK >= TO_NUMBER(#thrhld#) GROUP BY A.GROUP2
